//! Stub generator binary for MathHook Python bindings
//!
//! Generates .pyi type stub files for Python IDE support.
//!
//! Usage:
//!   cargo run --bin stub_gen -p mathhook-python
//!
//! Output:
//!   Creates mathhook.pyi in the crate root directory.

use pyo3_stub_gen::Result;

fn main() -> Result<()> {
    // Get stub info from the library (generated by define_stub_info_gatherer! macro)
    let stub_info = mathhook_python::stub_info()?;

    // Generate stubs - this writes .pyi files based on pyproject.toml config
    // or defaults to the current directory
    stub_info.generate()?;

    println!("Python type stubs generated successfully!");
    Ok(())
}
